  transition: background-color 0.2s;
}

.social-icons a:hover {
  background-color: rgba(255, 255, 255, 0.1);
}

/* Red Glitch Effect for Social Media Icons */
.glitch-icon {
  position: relative;
  animation: glitch-static 25s infinite;
}

.glitch-icon::before,
.glitch-icon::after {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: inherit;
  border-radius: inherit;
  pointer-events: none;
}

.glitch-icon::before {
  animation: glitch-red 20s infinite;
  filter: hue-rotate(90deg) saturate(200%) brightness(1.2);
  mix-blend-mode: screen;
}

.glitch-icon::after {
  animation: glitch-shift 18s infinite;
  filter: hue-rotate(270deg) saturate(150%) brightness(0.8);
  mix-blend-mode: multiply;
}

@keyframes glitch-static {
  0%, 96%, 100% {
    transform: translate(0);
    filter: hue-rotate(0deg) saturate(100%) contrast(100%);
  }
  97% {
    transform: translate(-2px, 2px) scale(1.02);
    filter: hue-rotate(180deg) saturate(300%) contrast(150%) brightness(1.3);
  }
  98% {
    transform: translate(3px, -2px) scale(0.98);
    filter: hue-rotate(270deg) saturate(400%) contrast(200%) brightness(0.7);
  }
  99% {
    transform: translate(-2px, -3px) scale(1.01);
