/* River Flow Animation - Smooth Continuous Movement */
@keyframes river-flow {
  0% { transform: translateX(-100%) scaleX(1) rotateZ(0deg); }
  25% { transform: translateX(-50%) scaleX(1.1) rotateZ(0.5deg); }
  50% { transform: translateX(0%) scaleX(1.05) rotateZ(0deg); }
  75% { transform: translateX(50%) scaleX(1.08) rotateZ(-0.3deg); }
  100% { transform: translateX(100%) scaleX(1) rotateZ(0deg); }
}

/* Distorted River Glitch */
@keyframes river-distort {
  0%, 100% { 
    filter: hue-rotate(0deg) contrast(1) saturate(1) brightness(1);
    transform: scaleY(1) skewX(0deg);
  }
  15% { 
