/* Glitch Effects - RGB Channel Separation & VHS Style */

/* Text Glitch Effect */
@keyframes text-glitch {
  0%, 96%, 100% {
    transform: translate(0);
    filter: hue-rotate(0deg) saturate(100%) contrast(100%);
  }
  97% {
    transform: translate(-2px, 2px);
    filter: hue-rotate(240deg) saturate(400%) contrast(180%) brightness(1.4);
  }
  98% {
    transform: translate(3px, -2px);
    filter: hue-rotate(120deg) saturate(350%) contrast(220%) brightness(0.6);
  }
  99% {
    transform: translate(-2px, -2px);
    filter: hue-rotate(300deg) saturate(500%) contrast(150%) brightness(1.6) sepia(0.3);
  }
}

/* RGB Channel Separation */
@keyframes rgb-shift {
  0%, 95%, 100% {
    transform: translate(0);
    text-shadow: none;
  }
  2% {
    transform: translate(-0.5px, 0);
    text-shadow: 2px 0 #ff0000, -2px 0 #00ffff;
  }
  4% {
    transform: translate(0.5px, 0);
    text-shadow: -1px 0 #ff00ff, 1px 0 #00ff00;
  }
  6% {
    transform: translate(-1px, 0);
    text-shadow: 1.5px 0 #ff0000, -1.5px 0 #0000ff;
  }
}

/* VHS Scan Lines */
@keyframes vhs-scan {
  0% {
    transform: translateY(-100vh);
    opacity: 0.8;
  }
  50% {
    opacity: 0.3;
  }
  100% {
    transform: translateY(100vh);
    opacity: 0.8;
  }
}

/* Digital Noise */
@keyframes digital-noise {
  0% {
    opacity: 0.1;
    transform: scale(1.02) rotate(0.5deg);
  }
  2% {
    opacity: 0;
    transform: scale(0.98) rotate(-0.3deg);
  }
  4% {
    opacity: 0.08;
    transform: scale(1.01) rotate(0.2deg);
  }
  6% {
    opacity: 0;
    transform: scale(1) rotate(0deg);
  }
  8%, 80%, 100% {
    opacity: 0;
    transform: scale(1) rotate(0deg);
  }
  85% {
    opacity: 0.1;
    transform: scale(1.02) rotate(0.5deg);
  }
  90% {
    opacity: 0;
    transform: scale(0.98) rotate(-0.3deg);
  }
  95% {
    opacity: 0.08;
    transform: scale(1.01) rotate(0.2deg);
  }
}

/* Chromatic Aberration */
@keyframes chromatic-aberration {
  0% {
    filter: hue-rotate(180deg) saturate(300%) contrast(160%) brightness(1.3);
    transform: scale(1.02) skewX(1deg);
  }
  2% {
    filter: hue-rotate(-120deg) saturate(400%) contrast(200%) brightness(0.7) invert(0.1);
    transform: scale(0.98) skewX(-2deg);
  }
  4% {
    filter: hue-rotate(240deg) saturate(350%) contrast(180%) brightness(1.5) sepia(0.4);
    transform: scale(1.03) skewY(1deg);
  }
  6% {
    filter: hue-rotate(-180deg) saturate(450%) contrast(220%) brightness(0.8) invert(0.2);
    transform: scale(0.97) skewY(-1deg);
  }
  8%, 90%, 100% {
    filter: none;
    transform: scale(1);
  }
  95% {
    filter: hue-rotate(180deg) saturate(300%) contrast(160%) brightness(1.3);
    transform: scale(1.02) skewX(1deg);
  }
  96% {
    filter: hue-rotate(-120deg) saturate(400%) contrast(200%) brightness(0.7) invert(0.1);
    transform: scale(0.98) skewX(-2deg);
  }
  97% {
    filter: hue-rotate(240deg) saturate(350%) contrast(180%) brightness(1.5) sepia(0.4);
    transform: scale(1.03) skewY(1deg);
  }
  98% {
    filter: hue-rotate(-180deg) saturate(450%) contrast(220%) brightness(0.8) invert(0.2);
    transform: scale(0.97) skewY(-1deg);
  }
}

/* Glitch Classes */
.glitch-text {
  position: relative;
  animation: text-glitch 30s infinite;
}

.glitch-text::before {
  content: attr(data-text);
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  color: #ff0000;
  mix-blend-mode: multiply;
  animation: rgb-shift 3s infinite;
  opacity: 0;
}

.glitch-text::after {
  content: attr(data-text);
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  color: #00ffff;
  mix-blend-mode: screen;
  animation: rgb-shift 4s infinite reverse;
  opacity: 0;
}

.glitch-text:hover::before,
.glitch-text:hover::after {
  opacity: 0.7;
}

.glitch-image {
  position: relative;
  animation: chromatic-aberration 35s infinite, image-rgb-shift 8s infinite;
  filter: contrast(1.1) saturate(1.2);
}

.glitch-image::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
