    rgba(255, 255, 255, 0.06) 2px,
    rgba(255, 255, 255, 0.12) 4px,
    rgba(255, 255, 255, 0.18) 6px,
    rgba(255, 255, 255, 0.12) 8px,
    rgba(255, 255, 255, 0.06) 10px,
    transparent 12px,
    transparent 16px
  );
  mask: radial-gradient(ellipse 210% 110% at 50% 50%, black 0%, transparent 78%);
  animation: flowing-waves-pulse 20s ease-in-out infinite;
  z-index: 1;
  pointer-events: none;
