/* Intermittent Flash Background Effect */
@keyframes flash-intermittent {
  0%, 94% { 
    opacity: 0; 
  }
  95%, 97% { 
    opacity: 0.15; 
  }
  98%, 100% { 
    opacity: 0; 
  }
}

.flash-bg {
  position: relative;
  overflow: hidden;
}

.flash-bg::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: radial-gradient(ellipse 80% 60% at 30% 40%, rgba(255, 255, 255, 0.8) 0%, transparent 70%);
  animation: flash-intermittent 8s ease-in-out infinite;
  z-index: 1;
  pointer-events: none;
}

.flash-bg::after {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: radial-gradient(ellipse 70% 50% at 70% 60%, rgba(255, 255, 255, 0.6) 0%, transparent 60%);
  animation: flash-intermittent 12s ease-in-out infinite;
  animation-delay: 4s;
  z-index: 2;
  pointer-events: none;
}

/* Hover Glitch Effects - Inspired by Attached Gif */
@keyframes hover-glitch-distort {
  0%, 100% {
    transform: translate(0);
    filter: hue-rotate(0deg) saturate(100%) contrast(100%);
  }
  10% {
    transform: translate(-2px, 1px) scaleX(1.02);
    filter: hue-rotate(280deg) saturate(300%) contrast(150%) brightness(1.2);
  }
  20% {
    transform: translate(3px, -2px) scaleX(0.98);
    filter: hue-rotate(180deg) saturate(400%) contrast(200%) brightness(0.8);
  }
  30% {
    transform: translate(-1px, 2px) scaleY(1.01);
    filter: hue-rotate(300deg) saturate(350%) contrast(180%) brightness(1.4);
  }
  40% {
    transform: translate(2px, -1px) scaleX(1.01);
    filter: hue-rotate(200deg) saturate(300%) contrast(160%) brightness(0.9);
  }
  50% {
    transform: translate(-3px, 0px) scaleY(0.99);
    filter: hue-rotate(320deg) saturate(450%) contrast(220%) brightness(1.3);
  }
  60% {
    transform: translate(1px, 2px) scaleX(0.99);
    filter: hue-rotate(240deg) saturate(380%) contrast(190%) brightness(0.7);
  }
  70% {
    transform: translate(-2px, -2px) scaleY(1.02);
    filter: hue-rotate(340deg) saturate(320%) contrast(170%) brightness(1.5);
  }
  80% {
    transform: translate(2px, 1px) scaleX(1.01);
    filter: hue-rotate(260deg) saturate(400%) contrast(200%) brightness(0.85);
  }
  90% {
    transform: translate(-1px, -1px) scaleY(0.98);
    filter: hue-rotate(300deg) saturate(350%) contrast(180%) brightness(1.1);
  }
}

@keyframes hover-lines-cyan-purple {
  0%, 100% {
    box-shadow: none;
    background-image: none;
  }
  25% {
    box-shadow: 
      inset 0 -2px 0 0 #00ffff,
      inset 0 -4px 0 0 rgba(255, 0, 255, 0.8),
      inset 0 2px 0 0 rgba(0, 255, 255, 0.6);
    background-image: 
      linear-gradient(90deg, transparent 0%, rgba(0, 255, 255, 0.3) 25%, transparent 50%, rgba(255, 0, 255, 0.3) 75%, transparent 100%);
  }
  50% {
    box-shadow: 
      inset 0 -1px 0 0 #ff00ff,
      inset 0 -6px 0 0 rgba(0, 255, 255, 0.7),
      inset 0 3px 0 0 rgba(255, 0, 255, 0.5);
    background-image: 
      linear-gradient(90deg, rgba(255, 0, 255, 0.4) 0%, transparent 30%, rgba(0, 255, 255, 0.4) 70%, transparent 100%);
  }
  75% {
    box-shadow: 
      inset 0 -3px 0 0 rgba(0, 255, 255, 0.9),
      inset 0 -1px 0 0 #ff00ff,
      inset 0 1px 0 0 rgba(0, 255, 255, 0.4);
    background-image: 
      linear-gradient(90deg, transparent 20%, rgba(255, 0, 255, 0.5) 40%, rgba(0, 255, 255, 0.5) 60%, transparent 80%);
  }
}

/* Hover Glitch Classes */
.hover-glitch-image {
  position: relative;
  transition: all 0.1s ease-out;
  overflow: hidden;
}

.hover-glitch-image:hover {
  animation: hover-glitch-distort 0.6s ease-in-out, hover-lines-cyan-purple 0.8s ease-in-out;
}

.hover-glitch-image:hover::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: 
    linear-gradient(90deg, 
      transparent 0%, 
      rgba(0, 255, 255, 0.2) 20%, 
      transparent 40%, 
      rgba(255, 0, 255, 0.3) 60%, 
      transparent 80%, 
      rgba(0, 255, 255, 0.1) 100%
