.hover-glitch-widget {
  position: relative;
  transition: all 0.1s ease-out;
  overflow: hidden;
}

.hover-glitch-widget:hover {
  animation: hover-glitch-distort 0.7s ease-in-out, hover-lines-cyan-purple 0.9s ease-in-out;
}

.hover-glitch-widget:hover::after {
