  }
  88% { 
    transform: translate(-1px, -2px) scale(1.01);
    filter: hue-rotate(0deg) saturate(250%) sepia(40%);
  }
}

@keyframes create-flash {
  0% { 
    opacity: 0.2;
    transform: translateX(-3px);
  }
  2% { 
    opacity: 0.15;
    transform: translateX(2px);
  }
  4%, 83%, 100% { 
    opacity: 0.08;
    transform: translateX(0);
  }
  89% { 
    opacity: 0.2;
    transform: translateX(-3px);
  }
  90% { 
    opacity: 0.15;
    transform: translateX(2px);
  }
}

/* Build Color Scale Keyframes - Blue/Cyan */
@keyframes build-color-glitch {
  0% { 
    transform: translate(-3px, 2px) scale(1.03);
    filter: hue-rotate(180deg) saturate(250%) sepia(40%);
  }
  3% { 
